css:
.footer-main {
  width: 100%;
  background-color: var(--muted);
  border-top: 1px solid var(--border);
  margin-top: auto;
}

.footer-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 2rem;
  margin-bottom: 2rem;
}

@media (min-width: 640px) {
  .footer-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 768px) {
  .footer-grid {
    grid-template-columns: repeat(4, 1fr);
    gap: 3rem;
  }
}

.footer-heading {
  font-size: 0.875rem;
  font-weight: 600;
  color: var(--foreground);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  margin-bottom: 0.5rem;
}

.footer-links {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
  list-style: none;
  padding: 0;
  margin: 0;
}

.footer-link {
  font-size: 0.875rem;
  color: var(--muted-foreground);
  text-decoration: none;
  transition: color 0.2s ease-in-out;
}

.footer-link:hover {
  color: var(--primary);
}

.footer-brand {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.footer-brand-logo {
  font-size: 1.25rem;
  font-weight: 700;
  color: var(--foreground);
}

.footer-brand-description {
  font-size: 0.875rem;
  color: var(--muted-foreground);
  line-height: 1.6;
  max-width: 300px;
}

.footer-social {
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
}

.footer-social-link {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 36px;
  height: 36px;
  border-radius: 0.375rem;
  background-color: var(--muted-foreground);
  color: var(--background);
  transition: all 0.2s ease-in-out;
  text-decoration: none;
}

.footer-social-link:hover {
  background-color: var(--primary);
  transform: translateY(-2px);
}

.footer-bottom {
  padding-top: 2rem;
  border-top: 1px solid var(--border);
  display: flex;
  flex-direction: column;
  gap: 1rem;
  align-items: center;
  justify-content: space-between;
}

@media (min-width: 768px) {
  .footer-bottom {
    flex-direction: row;
  }
}

.footer-copyright {
  font-size: 0.75rem;
  color: var(--muted-foreground);
  text-align: center;
}

@media (min-width: 768px) {
  .footer-copyright {
    text-align: left;
  }
}

.footer-legal-links {
  display: flex;
  gap: 1.5rem;
  list-style: none;
  padding: 0;
  margin: 0;
}

.footer-legal-link {
  font-size: 0.75rem;
  color: var(--muted-foreground);
  text-decoration: none;
  transition: color 0.2s ease-in-out;
}

.footer-legal-link:hover {
  color: var(--primary);
}
