.css-designer-layout {
  display: grid;
  grid-template-columns: 300px 1fr 400px;
  gap: 1rem;
  height: 100vh;
  padding: 1rem;
  background: var(--background);
}

@media (max-width: 1024px) {
  .css-designer-layout {
    grid-template-columns: 1fr;
    overflow-y: auto;
  }
}

.css-editor-panel {
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 1.5rem;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

.css-editor-panel-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 1rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid var(--border);
}

.css-editor-panel-title {
  font-weight: 600;
  font-size: 1rem;
  color: var(--foreground);
}

.css-editor-panel-actions {
  display: flex;
  gap: 0.5rem;
}

.css-color-picker {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  margin-bottom: 1rem;
}

.css-color-picker-label {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--foreground);
}

.css-color-picker-input {
  display: flex;
  gap: 0.75rem;
  align-items: center;
}

.css-color-picker-swatch {
  width: 40px;
  height: 40px;
  border-radius: var(--radius-md);
  border: 2px solid var(--border);
  cursor: pointer;
  transition: transform 0.2s ease-in-out;
}

.css-color-picker-swatch:hover {
  transform: scale(1.05);
}

.css-color-picker-value {
  flex: 1;
  padding: 0.5rem 0.75rem;
  border: 1px solid var(--border);
  border-radius: var(--radius-md);
  font-family: var(--font-mono, monospace);
  font-size: 0.875rem;
  background: var(--background);
}

.css-style-preview {
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 1.5rem;
  overflow-y: auto;
}

.css-style-preview-header {
  margin-bottom: 1.5rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid var(--border);
}

.css-style-preview-content {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.preview-section {
  padding: 1.5rem;
  border: 1px dashed var(--border);
  border-radius: var(--radius-md);
  background: var(--background);
}

.css-property-group {
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 1rem;
  margin-bottom: 1rem;
}

.css-property-group-header {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  margin-bottom: 1rem;
  cursor: pointer;
  user-select: none;
}

.css-property-group-icon {
  width: 24px;
  height: 24px;
  color: var(--primary);
}

.css-property-group-title {
  font-weight: 600;
  font-size: 0.875rem;
  color: var(--foreground);
  flex: 1;
}

.css-property-group-toggle {
  color: var(--muted-foreground);
  transition: transform 0.2s ease-in-out;
}

.css-property-group-toggle.expanded {
  transform: rotate(180deg);
}

.css-property-group-content {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.css-slider-control {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.css-slider-control-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.css-slider-control-label {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--foreground);
}

.css-slider-control-value {
  font-size: 0.875rem;
  font-family: var(--font-mono, monospace);
  color: var(--primary);
  font-weight: 600;
}

.css-slider-control-input {
  width: 100%;
  height: 4px;
  background: var(--muted);
  border-radius: 2px;
  outline: none;
  cursor: pointer;
}

.css-slider-control-input::-webkit-slider-thumb {
  appearance: none;
  width: 16px;
  height: 16px;
  background: var(--primary);
  border-radius: 50%;
  cursor: pointer;
  transition: transform 0.2s ease-in-out;
}

.css-slider-control-input::-webkit-slider-thumb:hover {
  transform: scale(1.2);
}

.css-slider-control-input::-moz-range-thumb {
  width: 16px;
  height: 16px;
  background: var(--primary);
  border: none;
  border-radius: 50%;
  cursor: pointer;
  transition: transform 0.2s ease-in-out;
}

.css-slider-control-input::-moz-range-thumb:hover {
  transform: scale(1.2);
}

.css-export-panel {
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 1.5rem;
  margin-top: 1rem;
}

.css-export-options {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-bottom: 1.5rem;
}

.css-export-option {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 1rem;
  border: 1px solid var(--border);
  border-radius: var(--radius-md);
  cursor: pointer;
  transition: all 0.2s ease-in-out;
}

.css-export-option:hover {
  border-color: var(--primary);
  background: var(--muted);
}

.css-export-option.selected {
  border-color: var(--primary);
  background: var(--accent);
}

.css-export-button {
  width: 100%;
  padding: 0.75rem;
  background: var(--primary);
  color: var(--primary-foreground);
  border: none;
  border-radius: var(--radius-md);
  font-weight: 600;
  cursor: pointer;
  transition: opacity 0.2s ease-in-out;
}

.css-export-button:hover {
  opacity: 0.9;
}

.css-code-output {
  background: var(--muted);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 1rem;
  font-family: var(--font-mono, monospace);
  font-size: 0.875rem;
  line-height: 1.6;
  overflow-x: auto;
  max-height: 400px;
  overflow-y: auto;
}

.css-code-output pre {
  margin: 0;
  color: var(--foreground);
}

.css-code-output code {
  font-family: inherit;
}

.css-db-indicator {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 0.75rem;
  background: var(--muted);
  border-radius: var(--radius-md);
  font-size: 0.75rem;
  color: var(--muted-foreground);
}

.css-db-indicator-dot {
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: var(--muted-foreground);
}

.css-db-indicator.synced .css-db-indicator-dot {
  background: var(--success, #22c55e);
  animation: pulse 2s infinite;
}

.css-db-indicator.error .css-db-indicator-dot {
  background: var(--destructive);
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}
