css:
.header-navigation {
  position: sticky;
  top: 0;
  z-index: 50;
  width: 100%;
  background-color: var(--background);
  border-bottom: 1px solid var(--border);
  backdrop-filter: blur(10px);
  background-color: rgba(255, 255, 255, 0.8);
}

@supports (backdrop-filter: blur(10px)) {
  .header-navigation {
    background-color: rgba(255, 255, 255, 0.8);
  }
}

.header-container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem 1.5rem;
  max-width: 1280px;
  margin: 0 auto;
}

@media (min-width: 768px) {
  .header-container {
    padding: 1rem 2rem;
  }
}

.header-logo {
  font-size: 1.5rem;
  font-weight: 700;
  color: var(--foreground);
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: opacity 0.2s ease-in-out;
}

.header-logo:hover {
  opacity: 0.8;
}

.header-nav-links {
  display: none;
  gap: 2rem;
  align-items: center;
}

@media (min-width: 768px) {
  .header-nav-links {
    display: flex;
  }
}

.header-nav-link {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--muted-foreground);
  text-decoration: none;
  transition: color 0.2s ease-in-out;
  position: relative;
}

.header-nav-link:hover {
  color: var(--foreground);
}

.header-nav-link::after {
  content: '';
  position: absolute;
  bottom: -4px;
  left: 0;
  width: 0;
  height: 2px;
  background: var(--primary);
  transition: width 0.2s ease-in-out;
}

.header-nav-link:hover::after {
  width: 100%;
}

.header-nav-link--active {
  color: var(--primary);
  font-weight: 600;
}

.header-nav-link--active::after {
  width: 100%;
}

.header-cta {
  padding: 0.5rem 1rem;
  font-size: 0.875rem;
  font-weight: 600;
  border-radius: 0.375rem;
  background: var(--primary);
  color: var(--primary-foreground);
  border: none;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  text-decoration: none;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
}

.header-cta:hover {
  opacity: 0.9;
  transform: translateY(-1px);
}

.header-cta:active {
  transform: translateY(0);
}
