:root {
  --background: oklch(0.98 0 0);
  --foreground: oklch(0.25 0 0);

  --card: oklch(1 0 0);
  --card-foreground: oklch(0.25 0 0);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.25 0 0);

  --primary: oklch(0.45 0.15 265);
  --primary-foreground: oklch(0.98 0 0);
  --secondary: oklch(0.65 0.02 250);
  --secondary-foreground: oklch(0.25 0 0);
  --muted: oklch(0.95 0.01 250);
  --muted-foreground: oklch(0.5 0.01 250);
  --accent: oklch(0.75 0.15 195);
  --accent-foreground: oklch(0.2 0.05 250);
  --destructive: oklch(0.55 0.22 25);
  --destructive-foreground: oklch(0.98 0 0);

  --border: oklch(0.88 0.01 250);
  --input: oklch(0.88 0.01 250);
  --ring: oklch(0.75 0.15 195);

  --sidebar: oklch(0.25 0.03 250);
  --sidebar-foreground: oklch(0.85 0.01 250);
  --sidebar-primary: oklch(0.75 0.15 195);
  --sidebar-primary-foreground: oklch(0.98 0 0);
  --sidebar-accent: oklch(0.35 0.04 250);
  --sidebar-accent-foreground: oklch(0.85 0.01 250);
  --sidebar-border: oklch(0.35 0.04 250);
  --sidebar-ring: oklch(0.75 0.15 195);

  --radius: 0.5rem;
}

.home-page {
  min-height: 100vh;
  background: linear-gradient(135deg, rgba(var(--color-primary-rgb), 0.05) 0%, transparent 50%, rgba(var(--color-accent-rgb), 0.05) 100%);
}

.hero-title {
  font-weight: 700;
  font-size: 2.5rem;
  line-height: 1.1;
  background: linear-gradient(90deg, var(--color-primary) 0%, var(--color-accent) 50%, var(--color-primary) 100%);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  color: transparent;
}

@media (min-width: 640px) {
  .hero-title {
    font-size: 3.5rem;
  }
}

@media (min-width: 768px) {
  .hero-title {
    font-size: 4rem;
  }
}

.hero-subtitle {
  color: var(--muted-foreground);
  font-weight: 400;
  max-width: 600px;
  font-size: 1rem;
  line-height: 1.6;
}

@media (min-width: 768px) {
  .hero-subtitle {
    font-size: 1.25rem;
  }
}

.feature-card {
  height: 100%;
  border: 2px solid var(--border);
  transition: border-color 0.2s ease-in-out, transform 0.2s ease-in-out;
}

.feature-card:hover {
  border-color: var(--primary);
  transform: translateY(-2px);
}

.feature-icon {
  width: 48px;
  height: 48px;
  border-radius: 0.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 700;
  font-size: 1.25rem;
  margin-bottom: 1rem;
}

.feature-card--level1 .feature-icon {
  background: linear-gradient(135deg, #3b82f6 0%, #2563eb 100%);
}

.feature-card--level2 .feature-icon {
  background: linear-gradient(135deg, #22c55e 0%, #16a34a 100%);
}

.feature-card--level3 .feature-icon {
  background: linear-gradient(135deg, #f97316 0%, #ea580c 100%);
}

.feature-card--level4 .feature-icon {
  background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
}

.feature-card--level5 .feature-icon {
  background: linear-gradient(135deg, #a855f7 0%, #9333ea 100%);
}

.feature-card--level6 .feature-icon {
  background: linear-gradient(135deg, #f59e0b 0%, #d97706 100%);
}
